mapper.select:
select
wu.id, wu.remarks, wu.qrcode_url, wu.InfoType, wu.city_id, wu.cinema_id, wu.weixin_groups_id groupId, wg1.parent_id parentId, wg1.group_name as wg1_name, wg2.group_name as wg2_name
from weixin_userinfo wu left join weixin_groups wg1 on wu.weixin_groups_id = wg1.id left join weixin_groups wg2 on wg1.parent_id = wg2.id where authorizerAppid = #{appId,jdbcType=VARCHAR}

