	#{authorizerRefreshToken,jdbcType=VARCHAR}


	and func_info = #{funcInfo,jdbcType=VARCHAR}


and  InfoType <> "unauthorized" 



	and PreAuthCode = #{PreAuthCode,jdbcType=VARCHAR}



